mapper.select:
SELECT member_id, username, email, password, phone_number, role FROM members
WHERE LOWER(email) LIKE LOWER('%' || #{serachString} || '%') OR LOWER(username) LIKE LOWER('%' || #{searchString} || '%') OR LOWER(phone_number) LIKE LOWER('%' || #{searchString} || '%')
ORDER BY member_id ASC;

SELECT member_id, username, email, password, phone_number, role
FROM members
WHERE members.member_id = #{id}

SELECT member_id, username, email, password, phone_number, role
FROM members
WHERE members.email = #{email}

mapper.insert:
INSERT INTO members(username, email, password, phone_number, role)
VALUES(#{username},
#{email}, #{passwordHash}, #{phoneNumber}, #{role});

mapper.update:
UPDATE members

	username = #{username},
	phone_number = #{phoneNumber},
	role = #{role},
	password = #{passwordHash}

WHERE member_id = #{id}
